    private final String monster;
    private final int slayerLevel;
    private final String[] locations;
    private final String[] itemsRequired;
    private final String[] attributes;
    private final String[] attackStyles;
    private final String[] alternatives;
    private final String[] slayerMasters;

    SlayerTaskMonster(String monster, int slayerLevel, String[] locations, String[] itemsRequired,
                      String[] attributes, String[] attackStyles, String[] alternatives, String[] slayerMasters) {
        this.monster = monster;
        this.slayerLevel = slayerLevel;
        this.locations = locations;
        this.itemsRequired = itemsRequired;
        this.attributes = attributes;
        this.attackStyles = attackStyles;
        this.alternatives = alternatives;
        this.slayerMasters = slayerMasters;
    }

    public String getMonster() {
        return monster;
    }

    public int getSlayerLevel() {
        return slayerLevel;
    }

    public String[] getLocations() {
        return locations;
    }

    public String[] getItemsRequired() {
        return itemsRequired;
    }

    public String[] getAttributes() {
        return attributes;
    }

    public String[] getAttackStyles() {
        return attackStyles;
    }

    public String[] getAlternatives() {
        return alternatives;
    }

    public String[] getSlayerMasters() {
        return slayerMasters;
    }

    // is valid monster
    public static boolean isValidMonster(String monster) {
        for (SlayerTaskMonster task : SlayerTaskMonster.values()) {
            if (task.getMonster().equals(monster)) {
                return true;
            }
            // check for alternatives
            for (String alternative : task.getAlternatives()) {
                if (alternative.equals(monster)) {
                    return true;
                }
            }
        }
        return false;
    }

    // Get item requirements for a monster by name
    public static String[] getItemRequirements(String monster) {
        for (SlayerTaskMonster task : SlayerTaskMonster.values()) {
            if (task.getMonster().equalsIgnoreCase(monster)) {
                return task.getItemsRequired();
            }
            // check for alternatives
            for (String alternative : task.getAlternatives()) {
                if (alternative.equalsIgnoreCase(monster)) {
                    return task.getItemsRequired();
                }
            }
        }
        return new String[]{"None"};
    }

    // Get monster by name
    public static SlayerTaskMonster getMonsterByName(String monster) {
        for (SlayerTaskMonster task : SlayerTaskMonster.values()) {
            if (task.getMonster().equals(monster)) {
                return task;
            }
            // check for alternatives
            for (String alternative : task.getAlternatives()) {
                if (alternative.equals(monster)) {
                    return task;
                }
            }
        }
        return null;
    }


